import "./App.css";
import React, { useState } from "react";
import Login from "./Login";
import Register from "./Register";
import Book from "./Book";
import Appointment from "./Appointment";
import Home from "./Home";
import {
  MDBContainer,
  MDBNavbar,
  MDBNavbarBrand,
  MDBNavbarToggler,
  MDBIcon,
  MDBNavbarNav,
  MDBNavbarItem,
  MDBNavbarLink,
  MDBBtn,
  MDBDropdown,
  MDBDropdownToggle,
  MDBDropdownMenu,
  MDBDropdownItem,
  MDBCollapse,
} from "mdb-react-ui-kit";
import Bk from './hos.jpg'
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";

function App() {
  const [auth, setAuth] = useState(false);
  const [authemail, setAuthemail] = useState(false);
  // Function to check user authentication status
async function checkAuthentication() {
  try {
    const authToken = localStorage.getItem('authToken');
    if (!authToken) {
      console.log('User is not authenticated');
      return;
    }
    const response = await fetch('/auth-endpoint', {
      headers: {
        Authorization: `Bearer ${authToken}`
      }
    });
    const data = await response.json();

    if (data.isAuthenticated) {
      console.log('User is authenticated');
      const authemail = localStorage.getItem('authemail');
      setAuthemail(authemail)
      setAuth(true)
    } else {
      console.log('User is not authenticated');
      setAuth(false)
    }
  } catch (error) {
    console.error(error);
  }
}
checkAuthentication()

// handle logout 
function handleLogout(){
  localStorage.removeItem('authToken');
  localStorage.removeItem('authemail')
  window.location.href = '/login';
  setAuth(false)
}

  const [showBasic, setShowBasic] = useState(false);
  return (
    <div className="App">
      <MDBNavbar expand="lg" light bgColor="light">
        <MDBContainer fluid>
          <MDBNavbarBrand href="/"><img
              src={Bk}
              height='35'
              alt=''
              loading='lazy'
            /></MDBNavbarBrand>

          <MDBNavbarToggler
            aria-controls="navbarSupportedContent"
            aria-expanded="false"
            aria-label="Toggle navigation"
            onClick={() => setShowBasic(!showBasic)}
          >
            <MDBIcon icon="bars" fas />
          </MDBNavbarToggler>

          <MDBCollapse navbar show={showBasic}>
            <MDBNavbarNav className="mr-auto mb-2 mb-lg-0">
              <MDBNavbarItem>
                <MDBNavbarLink active aria-current="page" href="/">
                  Home
                </MDBNavbarLink>
              </MDBNavbarItem>
              {auth === false && 
              <MDBNavbarItem>
                <MDBNavbarLink href="/login">Login</MDBNavbarLink>
              </MDBNavbarItem>
            }
             {auth === false && 
              <MDBNavbarItem>
                <MDBNavbarLink href="/register">Register</MDBNavbarLink>
              </MDBNavbarItem>
              }
              <MDBNavbarItem>
                <MDBNavbarLink href="/book">Book Appointment</MDBNavbarLink>
              </MDBNavbarItem>

              <MDBNavbarItem>
              <MDBDropdown>
                <MDBDropdownToggle tag='a' className='nav-link' role='button'>
                  More
                </MDBDropdownToggle>
                {auth === true &&
                <MDBDropdownMenu>
                  <MDBDropdownItem><div className="container text-center"><MDBBtn onClick={handleLogout}>Logout</MDBBtn></div></MDBDropdownItem>
                </MDBDropdownMenu>
                }
              </MDBDropdown>
            </MDBNavbarItem>
            </MDBNavbarNav>

            <form className="d-flex input-group w-auto">
              <input
                type="search"
                className="form-control"
                placeholder="Search Doctors"
                aria-label="Search"
              />
              <MDBBtn color="primary">Search</MDBBtn>
            </form>
          </MDBCollapse>
        </MDBContainer>
      </MDBNavbar>

      <Router>
        <Routes>
          <Route path="/" element={<Home authemail={authemail}/>}/>
          <Route path="/appbook/:username" element={<Appointment />}/>
          {auth === false && <Route path="/login" element={<Login />} />}
          {auth === false && <Route path="/Register" element={<Register />} />}
          {auth === true && <Route path="/book" element={<Book />} />}
          {auth === false && <Route path="/book" element={<Login />} />}
        </Routes>
      </Router>
    </div>
  );
}

export default App;
