import React, { useState } from 'react';
import Bk from './hos.jpg'
import axios from "axios";
import Alert from "react-bootstrap/Alert";
import {
  MDBBtn,
  MDBContainer,
  MDBRow,
  MDBCol,
  MDBCard,
  MDBCardBody,
  MDBCardImage,
  MDBInput,
  MDBIcon,
  MDBCheckbox
}
from 'mdb-react-ui-kit';

function Register() {
  // Function to check user authentication status
async function checkAuthentication() {
  try {
    const response = await fetch('/auth-endpoint');
    const data = await response.json();

    if (data.isAuthenticated) {
      // User is authenticated
      console.log('User is authenticated');
    } else {
      // User is not authenticated
      console.log('User is not authenticated');
    }
  } catch (error) {
    console.error(error);
  }
}

// Call the checkAuthentication() function to check the user's authentication status
checkAuthentication();
    const [registered, setRegistered] = useState(false);
    function rg(event){
        event.preventDefault();
        var email = document.getElementById("email").value;
        var password = document.getElementById("password").value;
        axios
        .post("/register", {
            email,password
        })
        .then((response) => {
          console.log(response.data);
          setRegistered(true)
        })
        .catch((error) => {
          console.log(error);
        });
    }
  return (
    <div className='App mt-4'>
    <MDBContainer fluid>
        {registered === false &&
      <MDBCard className='text-black m-5' style={{borderRadius: '25px'}}>
        <MDBCardBody>
          <MDBRow>
            <MDBCol md='10' lg='6' className='order-2 order-lg-1 d-flex flex-column align-items-center'>
            <form onSubmit={rg}>
              <p classNAme="text-center h1 fw-bold mb-5 mx-1 mx-md-4 mt-4">Sign up</p>

              {/* <div className="d-flex flex-row align-items-center mb-4 ">
                <MDBIcon fas icon="user me-3" size='lg'/>
                <MDBInput label='Your Name' id='form1' type='text' className='w-100'/>
              </div> */}

              <div className="d-flex flex-row align-items-center mb-4">
                <MDBIcon fas icon="envelope me-3" size='lg'/>
                <MDBInput label='Your Email' id='email' type='email' required/>
              </div>

              <div className="d-flex flex-row align-items-center mb-4">
                <MDBIcon fas icon="lock me-3" size='lg'/>
                <MDBInput label='Password' id='password' type='password' required/>
              </div>

              {/* <div className="d-flex flex-row align-items-center mb-4">
                <MDBIcon fas icon="key me-3" size='lg'/>
                <MDBInput label='Repeat your password' id='form4' type='password'/>
              </div> */}

              <div className='mb-4'>
                <MDBCheckbox name='flexCheck' value='' id='flexCheckDefault' label='Register' required />
              </div>

              <MDBBtn type="submit" className='mb-4' size='lg'>Register</MDBBtn>
              </form>
            </MDBCol>

            <MDBCol md='10' lg='6' className='order-1 order-lg-2 d-flex align-items-center'>
              <MDBCardImage src={Bk} fluid/>
            </MDBCol>

          </MDBRow>
        </MDBCardBody>
      </MDBCard>
    }
    {registered === true && <Alert variant='success'> Registered successfully</Alert>}

    </MDBContainer>
    </div>
  );
}

export default Register;
