import React, {useState} from "react";
import Alert from 'react-bootstrap/Alert';
import axios from "axios";
import { MDBInput, MDBCheckbox, MDBBtn } from "mdb-react-ui-kit";


function Createapp({ username, check, time, date }) {
    const [Post,setPosted] = useState("false");
    function patient_data(event){
        event.preventDefault();
        var patient_email = document.getElementById("patient_email").value
        var patient_name = document.getElementById("patient_name").value
        axios
        .post("/bookslot", {time,date,username,check,patient_email,patient_name
        })
        .then((response) => {
          console.log(response.data)
          setPosted("true")
        })
        .catch((error) => {
          console.log(error);
        });

    }
  return (
    <div className="App">
        <div className="container">
        <Alert variant="success">Slot is Available. You can now Book the Slot</Alert>
        {Post==="false" &&
      <form onSubmit={patient_data}>
        <MDBInput className="mb-4" id="patient_name" label="Name" required/>
        <MDBInput
          className="mb-4"
          type="email"
          id="patient_email"
          label="Email address"
          required
        />

        <MDBCheckbox
          wrapperClass="d-flex justify-content-center mb-4"
          id="form5Example3"
          label="I have read and agree to the terms"
          required
        />

        <MDBBtn type="submit" block>
          Submit
        </MDBBtn>
      </form>
        }
        {Post === "true" && <Alert variant="success">Slot Booked</Alert>}
      </div>
    </div>
  );
}
export default Createapp;
