import "./App.css";
import React, { useState } from "react";
import Login from "./Login";
import Register from "./Register";
import Book from "./Book";
import ViewBook from "./Book";
import Appointment from "./Appointment";
import Home from "./Home";
import {
  MDBContainer,
  MDBNavbar,
  MDBNavbarBrand,
  MDBNavbarToggler,
  MDBIcon,
  MDBFooter,
  MDBNavbarNav,
  MDBNavbarItem,
  MDBNavbarLink,
  MDBBtn,
  MDBDropdown,
  MDBDropdownToggle,
  MDBDropdownMenu,
  MDBDropdownItem,
  MDBCollapse,
} from "mdb-react-ui-kit";
import Bk from './hos.jpg'
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";

function App() {
  const [auth, setAuth] = useState(false);
  const [authemail, setAuthemail] = useState(false);
  // Function to check user authentication status
async function checkAuthentication() {
  try {
    const authToken = localStorage.getItem('authToken');
    if (!authToken) {
      console.log('User is not authenticated');
      return;
    }
    const response = await fetch('/auth-endpoint', {
      headers: {
        Authorization: `Bearer ${authToken}`
      }
    });
    const data = await response.json();

    if (data.isAuthenticated) {
      console.log('User is authenticated');
      const authemail = localStorage.getItem('authemail');
      setAuthemail(authemail)
      setAuth(true)
    } else {
      console.log('User is not authenticated');
      setAuth(false)
    }
  } catch (error) {
    console.error(error);
  }
}
checkAuthentication()

// handle logout 
function handleLogout(){
  localStorage.removeItem('authToken');
  localStorage.removeItem('authemail')
  window.location.href = '/login';
  setAuth(false)
}

  const [showBasic, setShowBasic] = useState(false);
  return (
    <div className="App">

      {/* navbar */}
      <MDBNavbar expand="lg" light bgColor="light">
        <MDBContainer fluid>
          <MDBNavbarBrand href="/"><img
              src={Bk}
              height='35'
              alt=''
              loading='lazy'
            /></MDBNavbarBrand>

          <MDBNavbarToggler
            aria-controls="navbarSupportedContent"
            aria-expanded="false"
            aria-label="Toggle navigation"
            onClick={() => setShowBasic(!showBasic)}
          >
            <MDBIcon icon="bars" fas />
          </MDBNavbarToggler>

          <MDBCollapse navbar show={showBasic}>
            <MDBNavbarNav className="mr-auto mb-2 mb-lg-0">
              <MDBNavbarItem>
                <MDBNavbarLink active aria-current="page" href="/">
                  Home
                </MDBNavbarLink>
              </MDBNavbarItem>
              {auth === false && 
              <MDBNavbarItem>
                <MDBNavbarLink href="/login">Login</MDBNavbarLink>
              </MDBNavbarItem>
            }
             {auth === false && 
              <MDBNavbarItem>
                <MDBNavbarLink href="/register">Register</MDBNavbarLink>
              </MDBNavbarItem>
              }
              {auth === false && 
              <MDBNavbarItem>
                <MDBNavbarLink href="/viewbook">View Appointments</MDBNavbarLink>
              </MDBNavbarItem>
              }
              <MDBNavbarItem>
                <MDBNavbarLink href="/book">Book Appointment</MDBNavbarLink>
              </MDBNavbarItem>

              <MDBNavbarItem>
              <MDBDropdown>
                <MDBDropdownToggle tag='a' className='nav-link' role='button'>
                  More
                </MDBDropdownToggle>

                <MDBDropdownMenu>
                  <MDBDropdownItem>
                  {auth === true && 
                    <MDBBtn color="white" onClick={handleLogout} className="w-100">Logout</MDBBtn>
                  } 
                    </MDBDropdownItem>
                </MDBDropdownMenu>

              </MDBDropdown>
            </MDBNavbarItem>
            </MDBNavbarNav>


            <form className="d-flex input-group w-auto">
              <input
                type="search"
                className="form-control"
                placeholder="Search Doctors"
                aria-label="Search"
              />
              <MDBBtn color="primary">Search</MDBBtn>
            </form>

          </MDBCollapse>
        </MDBContainer>
      </MDBNavbar>

      {/* routes */}
      <Router>
        <Routes>
          <Route path="/" element={<Home authemail={authemail}/>}/>
          <Route path="/appbook/:username" element={<Appointment />}/>
          {auth === false && <Route path="/login" element={<Login />} />}
          {auth === false && <Route path="/Register" element={<Register />} />}
          {auth === true && <Route path="/book" element={<Book />} />}
          {auth === false && <Route path="/viewbook" element={<Viewbook />} />}
          {auth === false && <Route path="/book" element={<Login />} />}
        </Routes>
      </Router>


      {/* footer */}
      <br></br>
      <MDBFooter bgColor='light' className='text-center text-lg-start text-muted'>
      <section className='d-flex justify-content-center justify-content-lg-between p-4 border-bottom'>
        <div className='me-5 d-none d-lg-block'>
          <span>Get connected with me on social networks:</span>
        </div>

        <div>
          <a href='mailto:naveedqadir0@gmail.com' className='me-4 text-reset'>
            <MDBIcon fab icon="google" />
          </a>
          <a href='https://www.linkedin.com/in/naveedqadir/' target="blank" className='me-4 text-reset'>
            <MDBIcon fab icon="linkedin" />
          </a>
          <a href='https://github.com/naveedqadir' target="blank" className='me-4 text-reset'>
            <MDBIcon fab icon="github" />
          </a>
        </div>
      </section>
      <div className='text-center p-4' style={{ backgroundColor: 'rgba(0, 0, 0, 0.04)' }}>
        © 2021 Copyright:
        <a className='text-reset fw-bold' href='http://localhost:3000'>
          Appolo
        </a><i className="mx-4">Built By Naveed Qadir</i> 
      </div>
    </MDBFooter>
    </div>
  );
}

export default App;
