import React, { useState } from "react";
import Alert from "react-bootstrap/Alert";
import axios from "axios";
import { MDBInput, MDBCheckbox, MDBBtn } from "mdb-react-ui-kit";
import {
  MDBCard,
  MDBCardBody,
  MDBCardTitle,
  MDBCardText,
  MDBCardHeader,
  MDBRipple,
  MDBRow,
  MDBCol,
} from "mdb-react-ui-kit";

function Createapp({ username, check, time, date }) {
  const [Post, setPosted] = useState("false");
  const [details, setDetails] = useState("false");
  function patient_data(event) {
    event.preventDefault();
    var patient_email = document.getElementById("patient_email").value;
    var patient_name = document.getElementById("patient_name").value;
    axios
      .post("/bookslot", {
        time,
        date,
        username,
        check,
        patient_email,
        patient_name,
      })
      .then((response) => {
        console.log(response.data);
        setDetails(response.data);
        setPosted("true");
      })
      .catch((error) => {
        console.log(error);
        setPosted("false");
      });
  }
  return (
    <div className="App">
      <div className="container">
        {Post === "false" && (
          <Alert variant="success">
            Slot is Available. You can now Book the Slot
          </Alert>
        )}
        {Post === "false" && (
          <form onSubmit={patient_data}>
            <MDBInput
              className="mb-4"
              id="patient_name"
              label="Name"
              required
            />
            <MDBInput
              className="mb-4"
              type="email"
              id="patient_email"
              label="Email address"
              required
            />

            <MDBCheckbox
              wrapperClass="d-flex justify-content-center mb-4"
              id="form5Example3"
              label="I have read and agree to the terms"
              required
            />

            <MDBBtn type="submit" block>
              Submit
            </MDBBtn>
          </form>
        )}
        {Post === "true" && <Alert variant="success">Slot Booked</Alert>}
        {Post === "true" && 
        <div className="container">
                <MDBRow>
        {details.map((doc) => (
          <MDBCol size="3" className="container" key={doc._id}>
              <MDBCard background='light' shadow='0' border='dark' className="">
                <MDBCardHeader><h3>{doc.name}</h3></MDBCardHeader>
                <MDBRipple
                  rippleColor="light"
                  rippleTag="div"
                  className="bg-image hover-overlay"
                >
                  {/* <MDBCardImage src="" fluid alt="..." />
                  <a>
                    <div
                      className="mask"
                      style={{ backgroundColor: "rgba(251, 251, 251, 0.15)" }}
                    ></div>
                  </a> */}
                </MDBRipple>
                <MDBCardBody>
                  <MDBCardTitle>{doc.catagory}</MDBCardTitle>
                  <MDBCardText>{doc.degree}</MDBCardText>
                  <Link to={`/appbook/${doc.username}`}><MDBBtn>Book Appointment</MDBBtn></Link>
                </MDBCardBody>
              </MDBCard>
            </MDBCol>
        ))}
      </MDBRow>

          </div>
        }
      </div>
    </div>
  );
}
export default Createapp;
