import "./App.css";
import React, { useState } from "react";
import Autosuggest from 'react-autosuggest';
import axios from "axios";
import Login from "./Login";
import Register from "./Register";
import Book from "./Book";
import Viewbook from "./Viewbook";
import Appointment from "./Appointment";
import Home from "./Home";
import {
  MDBContainer,
  MDBNavbar,
  MDBNavbarBrand,
  MDBNavbarToggler,
  MDBIcon,
  MDBNavbarNav,
  MDBNavbarItem,
  MDBNavbarLink,
  MDBBtn,
  MDBDropdown,
  MDBDropdownToggle,
  MDBDropdownMenu,
  MDBDropdownItem,
  MDBCollapse,
} from "mdb-react-ui-kit";
import logo from './logo.jpg'
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import Footer from "./Footer";

function App() {
  const [auth, setAuth] = useState(false);
  const [authemail, setAuthemail] = useState(false);
  // Function to check user authentication status
  async function checkAuthentication() {
    try {
      const authToken = localStorage.getItem('authToken');
      if (!authToken) {
        console.log('User is not authenticated');
        return;
      }
      const response = await fetch('/auth-endpoint', {
        headers: {
          Authorization: `Bearer ${authToken}`
        }
      });
      const data = await response.json();

      if (data.isAuthenticated) {
        console.log('User is authenticated');
        const authemail = localStorage.getItem('authemail');
        setAuthemail(authemail)
        setAuth(true)
      } else {
        console.log('User is not authenticated');
        setAuth(false)
      }
    } catch (error) {
      console.error(error);
    }
  }
  checkAuthentication()

  return (
    <div className="App">



      {/* routes */}
      <Router>
        <Routes>
          <Route path="/" element={<Home authemail={authemail} />} />
          <Route path="/appbook/:username" element={<Appointment />} />
          {auth === false && <Route path="/login" element={<Login />} />}
          {auth === false && <Route path="/Register" element={<Register />} />}
          {auth === true && <Route path="/book" element={<Book />} />}
          {auth === false && <Route path="/book" element={<Login />} />}
          {auth === true && <Route path="/viewbook" element={<Viewbook />} />}
          {auth === false && <Route path="/Viewbook" element={<Login />} />}
        </Routes>
      </Router>


      {/* footer */}
      <br></br>
      <Footer />
    </div>
  );
}

export default App;
